import React from 'react';

class FormFavorito extends React.Component {
  render() {
    const { value, handleChange } = this.props
    return (
      <section>
        <label>
          Idade:
          <input
            type='number'
            name='idade'
            value={value}
            onChange={handleChange}
          />
        </label>
        <div className="input-group">
          <div className="input-group-prepend">
            <span className="input-group-text">Filme Favorito e NÃºmero da Sorte</span>
          </div>
            <input
              type='text'
              aria-label='filmeFavorito'
              className="form-control"
              value={value}
              onChange={handleChange}
            />
            <input
              type='number'
              aria-label='numeroDaSorte'
              className="form-control"
              value={value}
              onChange={handleChange}
            />
        </div>
      </section>
    );
  }
}

export default FormFavorito;
